configuration.jsScript:
var alarmTime = Number(metadata.ss_lastWaterTempAlarmTime);
var alarmDelay = Number(metadata.ss_clearAlarmTimeDelay);
return metadata.ts >= alarmDelay + alarmTime;

var msgtype = "POST_ATTRIBUTES_REQUEST";
var payload = {};
payload.lastWaterTempAlarmTime = metadata.ts;
return {
    msg: payload,
    metadata: metadata,
    msgType: msgtype
};

var newMsg = {};
var alarmStatus = "";
var alarmTemp = "";
if (metadata.isNewAlarm) {
    alarmStatus = "Triggered";
    alarmTemp = msg.details.water_temperature.toFixed(1);
} else {
    alarmStatus = "Cleared";
    alarmTemp = msg.details.clearedTemperature.toFixed(1);
}
newMsg.text = alarmStatus + ' "' + msg.name + '"' +
    " alarm for device: " + '"' + metadata.deviceName +
    '"' + " => water temperature: " + alarmTemp;
return {
    msg: newMsg,
    metadata: metadata,
    msgType: msgType
};

var lowTemp = Number(metadata.ss_lowWaterTempThreshold);
var highTemp = Number(metadata.ss_highWaterTempThreshold);
var msgTemp = msg.water_temperature;
return msgTemp < lowTemp || msgTemp > highTemp;

configuration.alarmDetailsBuildJs:
var details = {};
details.water_temperature = msg.water_temperature;
details.alarm_time = metadata.ts;
if (metadata.prevAlarmDetails) {
    var prevDetails = JSON.parse(metadata.prevAlarmDetails);
    details.count = prevDetails.count + 1;
} else {
    details.count = 1;
}
return details;

var details = {};
if (metadata.prevAlarmDetails) {
    details = JSON.parse(metadata.prevAlarmDetails);
}
details.clearedTemperature = msg.water_temperature;
return details;
